mapper.insert:
INSERT INTO e_board(
		e_board_key
	,	e_board_no
	,	e_board_title
	,	e_board_content
	,	e_board_date
	,	k_user_key
)VALUES(
		IF(#{eBoardKey} = "", NULL, #{eBoardKey})
	,(
		SELECT
			CONCAT(#{eBoardNo}, "-" ,LPAD(IFNULL(MAX(RIGHT(e_board_no,3)), 0) +1 , 3, 0))
		FROM e_board ALIAS_AS_SUBQUERY
		WHERE 1=1
		AND e_board_no LIKE CONCAT('%', #{eBoardNo} , '%')
	)
	,	#{eBoardTitle}
	,	#{eBoardContent}
	,	NOW()
	,	#{kUserKey}
)ON DUPLICATE KEY UPDATE
		e_board_title	= #{eBoardTitle}
	,	e_board_content	= #{eBoardContent}
	,	e_board_udate	= NOW()


INSERT INTO e_board_file(
	e_board_key
,	org_file_name
,	stored_file_name
,	file_size
,	file_date
)VALUES(
	#{eBoardKey}
,	#{org_file_name}
,	#{stored_file_name}
,	#{file_size}
,	NOW()
)

mapper.update:
UPDATE e_board_file 
SET del_gbn = 'Y'
WHERE file_key = #{fileKey}

UPDATE e_board_file
SET del_gbn = 'Y'
WHERE e_board_key = #{eBoardKey}
